select
  OBG_GENERATED_ID,
  CSP_NUMBER,
  CPA_TYPE,
  OTR_GENERATED_ID,
  OTY_TYPE,
  OTR_TYPE,
  DUE_DT
from
  CKT_DEBT_DTL with(readuncommitted)
where
  @otyType = OTY_TYPE and
  @obgGeneratedId = OBG_GENERATED_ID and
  @otrType = OTR_TYPE and
  @otrGeneratedId = OTR_GENERATED_ID and
  @cpaType = CPA_TYPE and
  @cspNumber = CSP_NUMBER

